function updateComments(){twikoo.getRecentComments({envId:"https://twikoo.iamlm.com",pageSize:5,includeReply:!1}).then(function(res){const latestCommentsContainer=document.getElementById("latestComments");res.forEach(function(comment){const commentLink=document.createElement("a");commentLink.className="flex items-center gap-4 px-4 py-3 rounded opacity-80 hover:opacity-100 hover:bg-stone-200 dark:hover:bg-stone-800",commentLink.href=comment.url+"#"+comment.id;const commentImage=document.createElement("div");commentImage.className="flex items-center self-center shrink-0";const image=document.createElement("img");image.src=comment.avatar,image.alt="Avatar",image.className="w-10 rounded",commentImage.appendChild(image);const commentContent=document.createElement("div");commentContent.className="flex flex-col gap-0 min-h-[2rem] items-start justify-center w-full min-w-0";const commentMeta=document.createElement("div");commentMeta.className="flex flex-row justify-between w-full";const commentTitle=document.createElement("h4");commentTitle.innerText=comment.nick,commentTitle.className="text-base font-medium";const commentTime=document.createElement("time");commentTime.innerText=comment.relativeTime,commentTime.className="text-sm opacity-40";const commentText=document.createElement("p");commentText.innerText=comment.commentText,commentText.className="w-full text-sm truncate",commentMeta.appendChild(commentTitle),commentMeta.appendChild(commentTime),commentContent.appendChild(commentMeta),commentContent.appendChild(commentText),commentLink.appendChild(commentImage),commentLink.appendChild(commentContent),latestCommentsContainer.appendChild(commentLink)})}).catch(function(err){console.error(err)})}window.addEventListener("load",updateComments)