function updateCommentsCount(){let liElements=document.querySelectorAll(".latestPosts li"),promises=[];liElements.forEach(function(li){let relPermalink=li.getAttribute("data-relpermalink"),promise=twikoo.getCommentsCount({envId:"https://twikoo.laomai.org",urls:[relPermalink],includeReply:!1});promises.push(promise)}),Promise.all(promises).then(function(responses){responses.forEach(function(res,index){let countElement=document.getElementById(liElements[index].getAttribute("data-relpermalink"));countElement&&(countElement.textContent=res[0].count)})}).catch(function(err){console.error(err)})}window.addEventListener("load",updateCommentsCount)