function updateCommentsCount(){let liElements=document.querySelectorAll(".archive-entry"),urls=[];liElements.forEach(function(li){let relPermalink=li.getAttribute("data-relpermalink");urls.push(relPermalink)}),twikoo.getCommentsCount({envId:"https://twikoo.laomai.org",urls,includeReply:!1}).then(function(responses){responses.forEach(function(res){let countElement=document.getElementById(res.url);countElement&&typeof res.count!="undefined"?countElement.textContent=res.count:console.error("Invalid response:",res)})}).catch(function(err){console.error(err)})}window.addEventListener("load",updateCommentsCount)